import React, { useEffect, useState, useReducer } from 'react'
import './home.css'
import Feed from './Feed'

export const ACTIONS = {
  SAVE: 'save',
  LIKE: 'like',

}

const Home = () => {

  const fetchApi = async ()=>{
    const rawData = await fetch('http://localhost:5001/api')
    const data = await rawData.json();


  }

  const [initialValue, setinitialValue] = useState();
  useEffect(()=>{
    fetchApi();
  })

  function reduce(state, action){
    switch (action.type){
      case ACTIONS.LIKE :

        return (state.map(data=>{
          if(data.id === action.payload.id){
            return {...data, liked: !data.liked}
          }else{
            return data;
          }
        })
      )
      case ACTIONS.SAVE:
        return (state.map(data=>{
          if(data.id===action.payload.id){
            console.log('saved');
            return {...data, saved: !data.saved}
          }
          return data;
        }))
    }

  }
  const [state, dispatch] = useReducer(reduce, [
    {
      user: 'Yugha',
      id: 1,
      post: 'https://slp-statics.astockcdn.net/static_assets/staging/24winter/home/curated-collections/card-2.jpg?width=580',
      saved: false,
      liked: false
    },
    {
      user: 'Ashwin',
      id: 2,
      post: 'https://images.unsplash.com/photo-1658242069270-61a1587cd6ef?q=80&w=1000&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D',
      saved: false,
      liked: false
    },
    {
      user: 'Aarif',
      id: 3,
      post: 'https://t4.ftcdn.net/jpg/02/55/54/13/360_F_255541389_7Vd0hVy25BEBA4uFL9WuLK6S5b7VM8ZK.jpg',
      saved: false,
      liked: false
    },
  ])

  return (
    <div className='container'>
      {state.map(data=>{
        return  <Feed fullData={{...data}} key={data.id} refer={data.id} dispatch={dispatch} />
      })
      }
    </div>
  )
}

export default Home
